declare
dbid          number;
dbname        varchar2(30);
instance_id   number := :INST_ID;
inst_name     varchar2(30);
task_name     varchar2(90);
start_id      number := :SNAP_ID;
end_id        number;
report_output clob;
begin
  dbms_output.enable(null);
  select distinct dbid, name into dbid, dbname from gv$database;
--select instance_number, instance_name into instance_id, inst_name from v$instance;
  select instance_name into inst_name from v$instance where instance_number = instance_id;
--select max(snap_id)-4,max(snap_id) into start_id, end_id from dba_hist_snapshot;
  end_id := start_id + 11;
  task_name := inst_name||'_'||start_id||'_'||end_id;
  DBMS_ADDM.DELETE(task_name);
  dbms_addm.analyze_inst(task_name, start_id, end_id);
  dbms_output.put_line('<PRE>');
  /*
  for rc in ( select output from 
    table(dbms_workload_repository.ash_report_text( dbid,instance_id,SYSDATE-31/1440, SYSDATE-1/1440))) loop
    dbms_output.put_line(rc.output);
  end loop;
  */
  SELECT DBMS_ADDM.GET_REPORT(task_name) into report_output FROM DUAL;
  dbms_output.put_line(report_output);
  dbms_output.put_line('</PRE>') ;
end;
--SELECT DBMS_ADDM.GET_REPORT(task_name) FROM DUAL;
