declare
  dbid    number;
  inst_id number;
  sid1    varchar2(30);
  sid2    number;
begin
  select distinct dbid into dbid from v$database;
  inst_id := :INST_ID;
  sid1    := to_char(:SID);
  sid2    := to_number(substr(sid1, 1, instr(sid1, ',',1)-1));
  dbms_output.enable(500000);
  dbms_output.put_line('<PRE>');
  for rc in ( select output from 
    table(dbms_workload_repository.ash_report_text( dbid,inst_id,SYSDATE-61/1440, SYSDATE-1/1440, L_SID=>sid2, L_SQL_ID=>:SQL_ID))) loop
    dbms_output.put_line(rc.output);
  end loop;
  dbms_output.put_line('</PRE>') ;
end;

SELECT gvs.inst_id , gvs.SID||','||gvs.SERIAL# SID , glo.os_user_name "OS User" , glo.oracle_username "DB User" , dbo.owner owner , dbo.object_name , dbo.object_type , drs.segment_name ROLLBACK_SEGMENT , gvt.used_urec "# of Records" , gvt.used_ublk "# of Blocks" , drs.tablespace_name FROM gv$locked_object glo , dba_objects dbo , dba_rollback_segs drs , gv$transaction gvt , gv$session gvs WHERE glo.object_id = dbo.object_id AND glo.xidusn = drs.segment_id AND glo.xidusn = gvt.xidusn AND glo.xidslot = gvt.xidslot AND gvt.addr = gvs.taddr AND gvs.inst_id = :INST_ID AND gvs.SID = substr(:SID, 1, instr(:SID, ',',1)-1) ORDER BY gvt.used_ublk DESC
