declare
  BIT      date;
  EIT      date;
  dbid     number;
  start_id number;
  end_id   number;
  inst_id  number;
begin
  select distinct dbid into dbid from v$database;
  start_id := to_number(:SNAP_ID-1);
  end_id   := to_number(start_id+4);
  inst_id  := :INST_ID;
  select
    BEGIN_INTERVAL_TIME into BIT
  from
    DBA_HIST_SNAPSHOT
  where
    SNAP_ID         = start_id and
    INSTANCE_NUMBER = inst_id;
  select
    END_INTERVAL_TIME into EIT
  from
    DBA_HIST_SNAPSHOT
  where
    SNAP_ID         = end_id and
    INSTANCE_NUMBER = inst_id;
  dbms_output.enable(500000);
  dbms_output.put_line('<PRE>');
  for rc in ( select output from 
    table(dbms_workload_repository.ash_report_text( dbid,inst_id,BIT, EIT, L_SQL_ID=>:SQL_ID))) loop
    dbms_output.put_line(rc.output);
  end loop;
  dbms_output.put_line('</PRE>') ;
end;

